local CountryService = {}

function CountryService.getCountryProfile(countryCode: string)
	return {
		["CountryCode"] = countryCode or "00",
		["CountryName"] = names[countryCode] or "NUL",
		["CountryFlag"] = emoji[countryCode] or "NUL"
	}
end

function CountryService.getUserCountryProfileAsync(player: Instance)
	return CountryService.getCountryProfile(game:GetService("LocalizationService"):GetCountryRegionForPlayerAsync(player))
end

return CountryService